draw((0,0)..(1,1)..(2,0)--(0,0));

draw((0.5,0)--(0.5,0.87)--(1.5,0.87)--(1.5,0));

draw(Circle((4,0),1));

pair A,B,C,D;

A=(3.3,0.7);
B=(3.3,-0.7);
D=(4.7,0.7);
C=(4.7,-0.7);

draw(A--B--C--D--A);

